import styled from "styled-components";
import shortid from "shortid";

const Chart = styled.div``;
function MakeRowsFromData(props) {
  let fullName = `${props.data.FirstName} ${props.data.LastName}`;

  return (
    <>
      <tr>
        <td>{fullName}</td>
        <td>{props.data.PhoneNumber}</td>
        <td>{props.data.Email}</td>
      </tr>
    </>
  );
}
function CustomerChart(props) {
  const data = props.data;

  return (
    <>
      <Chart>
        <h2>Customer Chart</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row) => (
              <MakeRowsFromData key={shortid.generate()} data={row} />
            ))}
          </tbody>
        </table>
      </Chart>
    </>
  );
}

export default CustomerChart;
