import { useState } from "react";
import styled from "styled-components";
import CustomerChart from "../comps/Home/CustomerChart";
import FormAddCustomer from "../comps/Home/FormAddCustomer";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Dashboard from "../comps/Home/Dashboard";

const Container = styled.div`
  display: flex;
  flex-flow: row;
  justify-content: space-between;

  .currentCustomers {
    flex-basis: 60%;
    background-color: lightgrey;
  }
  .quickAddCustomer {
    background-color: lightgrey;
    margin: 0 auto;
  }
`;

function Home() {
  return (
    <>
      <Router>
        <div className="Home">
          <header>
            <div>
              <h1>Basic CRM</h1>
            </div>
            <hr />
          </header>
          <Container>
            <Switch>
              <Route exact to="/" component={Dashboard} />
            </Switch>
          </Container>
        </div>
      </Router>
    </>
  );
}

export default Home;
