import React, { useState } from "react";
import styled from "styled-components";

const TablePageSel = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  min-width: 200px;
  width: 2em;
  margin-left: auto;
  margin-right: auto;

  .pageSelectorBtns {
    display: flex;
    align-items: center;
    justify-content: space-between;

    div:nth-child(2) {
      margin-left: 0.25em;
    }
  }
`;

function SelectTablePage(props) {
  const { customers } = props;
  const [currentPage, setCurrentPage] = useState(1);

  const maxCustTablePages = Math.ceil(customers.length / 10);
  return (
    <TablePageSel className="selectTablePage">
      <div className="pageSelectorBtns">
        <div>{"<<"}</div> <div>{"<"}</div>
      </div>
      <p>
        {currentPage} of {customers.length > 10 ? maxCustTablePages : 1}
      </p>
      <div className="pageSelectorBtns">
        <div>{">"}</div> <div>{">>"}</div>
      </div>
    </TablePageSel>
  );
}

export default SelectTablePage;
