import React, { useMemo } from "react";
import shortid from "shortid";
import Table, { useTable } from "react-table";

function CustomerTable(props) {
  const { customers } = props;

  const data = useMemo(
    () => [
      { name: "Jesse", favorite: "false" },
      { name: "Gaby", favorite: true },
      { name: "Mom", favorite: "false" },
      { name: "Dad", favorite: "false" },
      { name: "Melissa", favorite: "false" },
      { name: "Emily", favorite: "false" },
      { name: "Gisselle", favorite: "false" },
      { name: "Vixen", favorite: "false" },
    ],
    []
  );

  const columns = useMemo(
    () => [
      { Header: "Name", accessor: "name" },
      { Header: "Favorite", accessor: "favorite" },
    ],
    []
  );

  const table = useTable({ columns, data });

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = table;
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {
          // Loop over the table rows
          rows.map((row) => {
            // Prepare the row for display
            prepareRow(row);
            return (
              // Apply the row props
              <tr {...row.getRowProps()}>
                {
                  // Loop over the rows cells
                  row.cells.map((cell) => {
                    // Apply the cell props
                    return (
                      <td {...cell.getCellProps()}>
                        {
                          // Render the cell contents
                          cell.render("Cell")
                        }
                      </td>
                    );
                  })
                }
              </tr>
            );
          })
        }
      </tbody>
    </table>
  );
}

export default CustomerTable;
