import styled from "styled-components";
import CustomerChart from "../comps/Home/CustomerChart";

const customerArr = [];
const hasCustomers = customerArr.length > 0;

const Container = styled.div`
  display: ${hasCustomers ? "flex" : "block"};
  justify-content: space-between;

  .currentCustomers {
    flex-basis: 60%;
    background-color: lightgrey;
  }
  .quickAddCustomer {
    flex-basis: 30%;
    background-color: lightgrey;
  }
`;

function Home() {
  return (
    <>
      <div className="Home">
        <h1>Basic CRM</h1>
        <hr />
        <Container>
          {hasCustomers ? (
            <div className="currentCustomers">
              <CustomerChart />
            </div>
          ) : (
            <h2>There are no customers</h2>
          )}
          <div className="quickAddCustomer">Quick Add</div>
        </Container>
      </div>
    </>
  );
}

export default Home;
