import { useState } from "react";
import styled from "styled-components";
import CustomerChart from "../comps/Home/CustomerChart";
import FormAddCustomer from "../comps/Home/FormAddCustomer";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const Container = styled.div`
  display: flex;
  flex-flow: row;
  justify-content: space-between;

  .currentCustomers {
    flex-basis: 60%;
    background-color: lightgrey;
  }
  .quickAddCustomer {
    background-color: lightgrey;
    margin: 0 auto;
  }
`;

function Home() {
  const [Customers, setCustomers] = useState([
    { FirstName: "Bob", LastName: "Dylan", PhoneNumber: "9095550123" },
  ]);

  const handleFormData = (obj) => {
    console.log(obj);
    let amendedCustomerData = [obj, ...Customers];
    setCustomers(amendedCustomerData);
  };

  console.log(Customers);
  return (
    <>
      <Router>
        <div className="Home">
          <header>
            <h1>Basic CRM</h1>
          </header>
          <hr />
          <Container>
            <Switch>
              <Route exact to="/">
                {Customers.length > 0 && (
                  <div className="currentCustomers">
                    <CustomerChart data={Customers} />
                  </div>
                )}
                <div className="quickAddCustomer">
                  <FormAddCustomer
                    retrieveFormData={(data) => handleFormData(data)}
                  />
                </div>
              </Route>
            </Switch>
          </Container>
        </div>
      </Router>
    </>
  );
}

export default Home;
