import { useState } from "react";
import styled from "styled-components";
import CustomerChart from "../comps/Home/CustomerChart";
import FormAddCustomer from "../comps/Home/FormAddCustomer";

let customerArr = [];
let hasCustomers = customerArr.length > 0;

const Container = styled.div`
  display: ${hasCustomers ? "flex" : "block"};
  justify-content: space-between;

  .currentCustomers {
    flex-basis: 60%;
    background-color: lightgrey;
  }
  .quickAddCustomer {
    flex-basis: 30%;
    background-color: lightgrey;
  }
`;

function Home() {
  const [Customers, setCustomers] = useState(customerArr);

  const handleFormData = (obj) => {
    console.log(obj);
    let amendedCustomerData = [...Customers, obj];
    customerArr = amendedCustomerData;
    setCustomers(amendedCustomerData);
  };

  console.log(Customers);
  return (
    <>
      <div className="Home">
        <h1>Basic CRM</h1>
        <hr />
        <Container>
          {hasCustomers ? (
            <div className="currentCustomers">
              <CustomerChart />
            </div>
          ) : (
            <h2>There are no customers</h2>
          )}
          <div className="quickAddCustomer">
            <FormAddCustomer
              retrieveFormData={(data) => handleFormData(data)}
            />
          </div>
        </Container>
      </div>
    </>
  );
}

export default Home;
