import { useState } from "react";
import styled from "styled-components";

const Form = styled.form`
  max-width: 100%;
  margin: 0 auto;
  padding: 1em;
  display: flex;
  flex-flow: column nowrap;
  justify-content: flex-start;
  align-items: center;

  .formGroup {
    display: flex;
    width: 100%;
    margin: 0.25em auto;
    justify-content: center;
    align-items: center;
  }

  .submitGroup {
    margin: 0.3em auto;
  }

  input {
    margin-left: 0.5em;
    flex-grow: 1;
  }
`;

function FormAddCustomer(props) {
  const [FirstName, setFirstName] = useState("");
  const [LastName, setLastName] = useState("");
  const [PhoneNumber, setPhoneNumber] = useState("");
  const [Email, setEmail] = useState("");
  const [IsMissingInfo, setIsMissingInfo] = useState(false);

  function clearInputs() {
    setFirstName("");
    setLastName("");
    setPhoneNumber("");
    setEmail("");
    setIsMissingInfo(false);
  }

  function handleSubmit(event) {
    event.preventDefault();
    if (FirstName && LastName && PhoneNumber) {
      props.retrieveFormData({ FirstName, LastName, PhoneNumber, Email });
      clearInputs();
    } else {
      setIsMissingInfo(true);
    }
  }

  return (
    <>
      <Form className="addCustomer" onSubmit={handleSubmit}>
        <p>Customer's Information</p>
        {IsMissingInfo && <p>Missing Information!</p>}
        <div className="formGroup">
          <label htmlFor="customerFirstName">Name</label>
          <input
            type="text"
            id="customerFirstName"
            name="customerFirstName"
            value={FirstName}
            onChange={(e) => setFirstName(e.target.value)}
            placeholder="First Name"
          />
          <input
            type="text"
            id="customerLastName"
            name="customerLastName"
            value={LastName}
            onChange={(e) => setLastName(e.target.value)}
            placeholder="Last Name"
          />
        </div>
        <div className="formGroup">
          <label htmlFor="customerPhoneNumber">Phone Number</label>
          <input
            type="tel"
            id="customerPhoneNumber"
            name="customerPhoneNumber"
            value={PhoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            placeholder="Phone Number"
          />
        </div>
        <div className="formGroup">
          <label htmlFor="customerEmail">Email</label>
          <input
            type="email"
            id="customerEmail"
            name="customerEmail"
            value={Email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
          />
        </div>
        <div className="submitGroup">
          <input type="submit" value="Submit" />
        </div>
      </Form>
    </>
  );
}

export default FormAddCustomer;
