import React, { useMemo } from "react";
import shortid from "shortid";
import Table, { useTable } from "react-table";

function CustomerTable(props) {
  const { customers } = props;

  const data = useMemo(
    () => [
      { name: "Jesse", favorite: false },
      { name: "Gaby", favorite: true },
      { name: "Mom", favorite: false },
      { name: "Dad", favorite: false },
      { name: "Melissa", favorite: false },
      { name: "Emily", favorite: false },
      { name: "Gisselle", favorite: false },
      { name: "Vixen", favorite: false },
    ],
    []
  );

  const columns = useMemo(
    () => [
      { Header: "Name", accessor: "name" },
      { Header: "Favorite", accessor: "favorite" },
    ],
    []
  );

  const table = useTable({ columns, data });

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = table;
  return (
    <table>
      <thead>
        <tr>
          <th></th>
          <th>
            <input type="checkbox" name="" id="" />
          </th>
          <th></th>
          <th>Name</th>
          <th>Number of Interactions</th>
          <th>Last Interaction</th>
        </tr>
      </thead>
      <tbody>
        {customers.length > 10
          ? customers.map((customer, i) => {
              const mostRecentInteraction = customer.interactions.reduce(
                (t, s) => {
                  const currentDate = new Date(s);
                  const previousDate = new Date(t);
                  if (!previousDate) return currentDate;
                  else
                    return previousDate > currentDate
                      ? previousDate
                      : currentDate;
                }
              );

              return (
                <tr key={shortid.generate()}>
                  <td>{i + 1}</td>
                  <td>
                    <input type="checkbox" name="" id="" />
                  </td>
                  <td>
                    <i className="far fa-star"></i>
                  </td>
                  <td>{customer.name}</td>
                  <td>{customer.interactions.length}</td>
                  <td>{mostRecentInteraction.toLocaleDateString()}</td>
                </tr>
              );
            })
          : Array(10)
              .fill(0, 0, 10)
              .map((_, i) => {
                if (i < customers.length) {
                  const mostRecentInteraction = customers[
                    i
                  ].interactions.reduce((t, s) => {
                    const currentDate = new Date(s);
                    const previousDate = new Date(t);
                    if (!previousDate) {
                      return currentDate;
                    } else {
                      return previousDate > currentDate
                        ? previousDate
                        : currentDate;
                    }
                  });

                  return (
                    <tr key={shortid.generate()}>
                      <td>{i + 1}</td>
                      <td>
                        <input type="checkbox" name="" id="" />
                      </td>
                      <td>
                        <i className="far fa-star"></i>
                      </td>
                      <td>{customers[i].name}</td>
                      <td>{customers[i].interactions.length}</td>
                      <td>{mostRecentInteraction.toLocaleDateString()}</td>
                    </tr>
                  );
                } else {
                  return (
                    <tr key={shortid.generate()}>
                      <td>{i + 1}</td>
                      <td></td>
                      <td></td>
                      <td></td>
                      <td></td>
                      <td></td>
                    </tr>
                  );
                }
              })}
      </tbody>
    </table>
  );
}

export default CustomerTable;
